import React, { useRef, useState } from "react";
import { useHistory } from "react-router-dom";
import { FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import LoaderButton from "../components/LoaderButton";
import config from "../config";
import "./NewInference.css";
import { API } from "aws-amplify";
import { onError } from "../libs/errorLib";
import { Link } from "react-router-dom";

export default function NewInference() {
  const file = useRef(null);
  const [result, setResult] = useState(null);
  const history = useHistory();
  const [isLoading, setIsLoading] = useState(false);

  const image = file.current;

  function handleFileChange(event) {
    file.current = event.target.files[0];
  }

  async function handleSubmit(event) {
    event.preventDefault();

    setIsLoading(true);


    try {
      const response = await inference(image);

      setResult(response.name);
      setIsLoading(false)
      /* history.push("/result"); */

    } catch (e) {
        onError(e);
        setIsLoading(false);
      } 
  }

  /* headers: {
    "Access-Control-Allow-Origin": "*",
    "Access-Control-Allow-Credentials": "True",
    'Content-Type': 'image/png'
  }, */

  function inference(image) {
    return API.post("inference", ``, {
        headers: {"Content-Type": "image/png"},
        body: image
    });
  }

  return (
    <div className="NewInference">
      <form onSubmit={handleSubmit}>
        <FormGroup controlId="imageFile">
          <ControlLabel>{result}</ControlLabel>
          <FormControl onChange={handleFileChange} type="file" />
        </FormGroup>
        <LoaderButton
          block
          type="submit"
          bsSize="large"
          bsStyle="primary"
          isLoading={isLoading}
        >
         Detect
        </LoaderButton>
      </form>

    </div>
  );
  }
