import React, { useRef, useState } from "react";
import { Button, ButtonToolbar } from "react-bootstrap";
import { Tooltip, OverlayTrigger, Overlay } from "react-bootstrap";
import { Popover } from "react-bootstrap";
import ImageMapper from 'react-image-mapper';

import "./Architecture.css";

export default function Architecture() {

  const [popover, setPopover] = useState("blank");
  const URL = "https://demo-object-detection-app.s3.amazonaws.com/architecture3.png";

  const MAP = {
    name: "my-map",
    areas: [
      { name: "1", shape: "circle", coords: [137,58,10], strokeColor: "green" },
      { name: "2", shape: "circle", coords: [247,198.5,10], strokeColor: "green"  },
      { name: "3", shape: "circle", coords: [485,198,10], strokeColor: "green"  },
      { name: "4", shape: "circle", coords: [621,330.5,10], strokeColor: "green"  },
      { name: "5", shape: "circle", coords: [484,259,10], strokeColor: "green" },
      { name: "6", shape: "circle", coords: [250,259,10], strokeColor: "green" },
    ]
  };

  function choosePopover(pop_num) {

    const popoverContent = {
      "blank": "",
      "1": "The browser downloads and stores the static content that forms this website. \
            The user interface is written in React.js, while runtime environment uses Node.js platform. \
            Open-source AWS framework Amplify is used to make API calls.",

      "2": "Within a click of a button the uploaded image is base64 encoded for safe transport through the web \
            and AWS Amplify makes a REST API call to API Gateway, set up as Lambda Proxy integration.",

      "3": "API Gateway invokes Lambda function that hosts Python code used to make machine learning inference.",
      "4": "The inference code was written by me during the “Deep Learning Specialization” course on coursera.org, inspired by this project of Allan Zelener. \
            To make predictions I use trained machine learning model YOLOv2 available here, authored by Joseph Redmon. \
            YOLOv2 detects thousands of potential objects on the input image by specifying bounding box coordinates relative to image dimensions. \
            Thanks to non-max-suppression technique, Lambda outputs only the most probable predictions.\
            Due to bulky both model weights and library dependencies, Amazon EFS is used to extend Lambda storage space.",

      "5": "Lambda returns the input image with drawn bounding boxes and marked labels to indicate predicted objects. For safe web transport to the user, the image is again base64 encoded.",

      "6": "API Gateway passes the response generated by Lambda to the front-end."
    };

    const popoverTitle = {
      "0": "",
      "1": "Static website hosting on S3",
      "2": "API Gateway call via Amplify",
      "3": "API Lambda Proxy integration",
      "4": "Lambda intergrated with EFS for machine learning inference ",
      "5": "Lambda integration response",
      "6": "API Gateway response via Amplify"
    };


    return (
      <Popover id={pop_num} title={popoverTitle[pop_num]} >
        {popoverContent[pop_num]}
      </Popover>
    );

  }
  const header = "The app is built on top of serverless-stack bundle, a part of the Serverless Stack open-source project developed by Anomaly Detection. \
                 They created a step-by-step guide to create a full-stack serverless application hosted on AWS. You can check it out here."


  function handleClick(event) {
    const pop_num = event.name.toString();
    setPopover(pop_num);
    choosePopover("1");
  };

  return (
    <body>
      <p> {popover} {header }</p>
      <div className="image">

          <ImageMapper src={URL} map={MAP} width={794} onClick={handleClick}/>

      </div>
    </body>
  );
}

