import React, { useRef, useState } from "react";
import { useHistory } from "react-router-dom";
import { FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import LoaderButton from "../components/LoaderButton";
import config from "../config";
import "./NewInference.css";
import { API } from "aws-amplify";
import { onError } from "../libs/errorLib";
import { Link } from "react-router-dom";
import {Jumbotron, Row, Col} from 'react-bootstrap'
import {Button} from 'react-bootstrap'
import {ProgressBar} from 'react-bootstrap'


export default function NewInference() {
  const file = useRef(null);
  const [outputImage, setOutputImage] = useState(null);
  const [inputImage, setInputImage] = useState(null);
  const history = useHistory();
  const [isLoading, setIsLoading] = useState(false);
  const [inferenceMade, setInferenceMade] = useState(false);


  function handleFileChange(event) {

    file.current = event.target.files[0];

    if (file.current.type != "image/png" || "image/jpeg" || "image/jpg") {

      alert(
        ` Pick a JPG or PNG file `
      );


    return;
    }


    var reader = new FileReader();
    reader.readAsDataURL(file.current);
    reader.onloadend = function() {
      const base64string = reader.result;
      // console.log("Base64 String - ", base64string);
      var imageBase64 = base64string.substr(base64string.indexOf(',') + 1);
      setInputImage(imageBase64);
      // console.log('Base64 String without Tags- ', imageBase64);
    }
  }

  async function handleSubmit(event) {
    event.preventDefault();




    setIsLoading(true);

    try {

      while (outputImage==null) {
        let response = await inference(inputImage);
        setOutputImage(response);
        setInferenceMade(true);
      }
    } catch (e) {
      onError(e);
    }
    setIsLoading(false);
  }

  function inference(inputImage) {
    return API.post("inference", ``, {
      headers: {
        "Content-Type": "application/json", 
        "Accept": "application/json"
      },
      body: inputImage
    });

  }

  function validateForm() {

    return inputImage!=null && file.type == ( "image/png" || "image/jpg" || "image/jpeg" )
  }

  return (
    <body>
      <div>
        {inferenceMade ? (
          <>
            <Jumbotron className="PostInference">
              <Row> 
                <Col xs={5} md={5}>
                <h4>Liked it?</h4>
                <h5>If you are curious, learn more about the architecture of the app.</h5>
              </Col>
              <Col xs={5} md={5}>
                <p>
                  <Button size="sm" variant="primary">Learn more</Button>
                </p>
              </Col>
              </Row>
            </Jumbotron>
          </>
          ) : null }
      </div>
      <div className="NewInference">
        <form>
          <FormGroup controlId="imageFile">
            <ControlLabel>Choose an image to process (JPG or PNG)</ControlLabel>
            <FormControl autoFocus size="lg" onChange={handleFileChange} type="file" />
          </FormGroup>
          <p>{ isLoading ? "Detecting objects ..." : "" }</p>
        </form>
      </div>
      <div className="Button">
        <form onSubmit={handleSubmit}>
        <LoaderButton
            type="submit"
            bsSize="medium"
            bsStyle="danger"
            isLoading={isLoading}
            disabled={inputImage==null}
          >
          Detect objects
          </LoaderButton>
        </form>
      </div>
      <img src={outputImage == null ? "" : `data:image/png;base64,${outputImage}`} />
    </body>
  );
  }

