import React, { useRef, useState } from "react";
import { useHistory } from "react-router-dom";
import { FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import LoaderButton from "../components/LoaderButton";
import config from "../config";
import "./NewInference.css";
import { API } from "aws-amplify";
import { onError } from "../libs/errorLib";
import { Link } from "react-router-dom";

export default function NewInference() {
  const file = useRef(null);
  const [outputImage, setOutputImage] = useState(null);
  const [inputImage, setInputImage] = useState(null);
  const history = useHistory();
  const [isLoading, setIsLoading] = useState(false);


  function handleFileChange(event) {
    file.current = event.target.files[0];
    console.log(file.current);

    var reader = new FileReader();
    reader.readAsDataURL(file.current);
    reader.onloadend = function() {
      const base64string = reader.result;
      // console.log("Base64 String - ", base64string);
      var imageBase64 = base64string.substr(base64string.indexOf(',') + 1);
      setInputImage(imageBase64);
      // console.log('Base64 String without Tags- ', imageBase64);
    }
  }

  async function handleSubmit(event) {
    event.preventDefault();

    setIsLoading(true);

    try {

      let response = await inference(inputImage);
      setOutputImage(response);

    } catch (e) {
      onError(e);
    }
    setIsLoading(false);
  }

  function inference(inputImage) {
    return API.post("inference", ``, {
      headers: {
        "Content-Type": "application/json", 
        "Accept": "application/json"
      },
      body: inputImage
    });

  }



  function renderImage(image) {
    return (
      <div>
        <img src={`data:image/png;base64,${outputImage}`} />
      </div>
    );
  } 
  return (
    <div className="NewInference">
      <form onSubmit={handleSubmit}>
        <FormGroup controlId="imageFile">
          <ControlLabel>Select input image </ControlLabel>
          <FormControl onChange={handleFileChange} type="file" />
        </FormGroup>
        <LoaderButton
          type="submit"
          bsSize="large"
          bsStyle="primary"
          isLoading={isLoading}
        >
         Detect
        </LoaderButton>
      </form>
      <h4>
       { outputImage == null ? "" : renderImage(outputImage) }
      </h4>

    </div>
  );
  }
