import React, { useRef, useState } from "react";
import { useHistory } from "react-router-dom";
import { FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import LoaderButton from "../components/LoaderButton";
import config from "../config";
import "./NewInference.css";
import { API } from "aws-amplify";
import { onError } from "../libs/errorLib";
import { Link } from "react-router-dom";
import {Jumbotron} from 'react-bootstrap'
import {Button} from 'react-bootstrap'
import { Modal } from 'react-bootstrap'


export default function NewInference() {
  const file = useRef(null);
  const [outputImage, setOutputImage] = useState(null);
  const [inputImage, setInputImage] = useState(null);
  const history = useHistory();
  const [isLoading, setIsLoading] = useState(false);


  function handleFileChange(event) {
    file.current = event.target.files[0];
    console.log(file.current);

    var reader = new FileReader();
    reader.readAsDataURL(file.current);
    reader.onloadend = function() {
      const base64string = reader.result;
      // console.log("Base64 String - ", base64string);
      var imageBase64 = base64string.substr(base64string.indexOf(',') + 1);
      setInputImage(imageBase64);
      // console.log('Base64 String without Tags- ', imageBase64);
    }
  }

  async function handleSubmit(event) {
    event.preventDefault();


    setIsLoading(true);

    try {

      while (outputImage==null) {
        let response = await inference(inputImage);
        setOutputImage(response);
      }
    } catch (e) {
      onError(e);
    }
    setIsLoading(false);
  }

  function inference(inputImage) {
    return API.post("inference", ``, {
      headers: {
        "Content-Type": "application/json", 
        "Accept": "application/json"
      },
      body: inputImage
    });

  }

  return (
    <body>
      <Jumbotron>
        <h4>Liked it?</h4>
        <p>
          Check the "How it works" section to see the architecture of the app.
        </p>
        <p>
          <Button variant="primary">Learn more</Button>
        </p>
      </Jumbotron>

      <Button variant="primary" onClick={handleShow}>
        Launch demo modal
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>For the curious ones</Modal.Title>
        </Modal.Header>
        <Modal.Body>Want to know how it works? Check out the app architecture.</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleNothanks}>
            No, thanks
          </Button>
          <Button variant="primary" onClick={handleClose}>
            OK
          </Button>
        </Modal.Footer>
      </Modal>

      <div className="NewInference">
        <form>
          <FormGroup controlId="imageFile">
            <ControlLabel>Choose an image to process (.png or .jpg)</ControlLabel>
            <FormControl autoFocus size="lg" onChange={handleFileChange} type="file" />
          </FormGroup>
          <p>{ isLoading ? "Detecting objects ..." : "" }</p>
        </form>
      </div>
      <div className="Button">
        <form onSubmit={handleSubmit}>
        <LoaderButton
            type="submit"
            bsSize="medium"
            bsStyle="danger"
            isLoading={isLoading}
            disabled={inputImage==null}
          >
          Detect objects
          </LoaderButton>
        </form>
      </div>
      <img src={outputImage == null ? "" : `data:image/png;base64,${outputImage}`} />
    </body>
  );
  }

