import React, { useRef, useState } from "react";
import { useHistory } from "react-router-dom";
import { FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import LoaderButton from "../components/LoaderButton";
import config from "../config";
import "./NewInference.css";
import { API } from "aws-amplify";
import { onError } from "../libs/errorLib";

export default function NewInference() {
  const file = useRef(null);
  const history = useHistory();
  const [isLoading, setIsLoading] = useState(false);


  function handleFileChange(event) {
    file.current = event.target.files[0];
  }

  async function handleSubmit(event) {
    event.preventDefault();

    if (file.current && file.current.size > config.IMAGE_FORMAT) {
      alert(
        `Pick a file in one of the following formats: ${config.FORMAT}.`
      );
      return;
    }

    setIsLoading(true);

    try {
        await makeInference({ file });

    } catch (e) {
        onError(e);
        setIsLoading(false);
    }
    }

    function makeInference(image) {
    return API.post("inference", "/inference", {
        body: ""
    });
    }

  return (
    <div className="NewInference">
      <form onSubmit={handleSubmit}>
        <FormGroup controlId="imageFile">
          <ControlLabel>Image</ControlLabel>
          <FormControl onChange={handleFileChange} type="file" />
        </FormGroup>
        <LoaderButton
          block
          type="submit"
          bsSize="large"
          bsStyle="primary"
          isLoading={isLoading}
        >
          Detect
        </LoaderButton>
      </form>
    </div>
  );
}
